# Exemplo de Seed. Antes de inserir verificar o user_merits_info_id e ao inserir uma achieve filho, alterar o child_id do pai.
User.all.each do |user|
	Achievement.create!(
	:user_merits_info_id => user.user_merits_info.id,
	:action_type => "review",
	:message => "Parabéns pelo cadastro. Esta é a sua barra de pontos. Comece escrevendo uma review e ganhe seus primeiros pontos.",
	:name => "review",
	:score => 5,
	:status => Achievement::STATUS[1])
	Achievement.create!(
	:user_merits_info_id => user.user_merits_info.id,
	:action_type => "indicacao",
	:message => "Que legal, a sua recomendação com certeza ajudará outras noivas. Agora deixe todas as suas amigas ficarem sabendo. Vá ao menu 'Meu Casamento' e indique uma amiga.",
	:name => "indicacao",
	:score => 5,
	:status => Achievement::STATUS[0])
	parent = user.user_merits_info.achievements.where("action_type = ?", "review")
	child = user.user_merits_info.achievements.where("action_type = ?", "indicacao")
	parent.first.update_attributes(:child_id => child.first.id)
end


